operation.add:
       $order_table = (DB_PREFIX) ? "order" : "`order`";
       $query = $this->db->query("SELECT nfe_info FROM " . DB_PREFIX . "$order_table WHERE order_id = $order_id");
       $url = $this->url->link('module/webmaniabrnfe/updateNFe', 'token=' . $this->session->data['token'], true);

       if(isset($query->rows[0]['nfe_info'])){
        $nfe_info = @unserialize($query->rows[0]['nfe_info']);
        if (!$nfe_info) {
          $nfe_info = @unserialize(utf8_decode(base64_decode($query->rows[0]['nfe_info'])));
        }
        if (!$nfe_info) {
          $nfe_info = array();
        }

        $custom_fields_customer = !empty($order_info['custom_field']) && is_array($order_info['custom_field']) ? $order_info['custom_field'] : null;
        if(!$custom_fields_customer){
          $customer_info = $this->model_sale_customer->getCustomer($order_info['customer_id']);
          $custom_fields_customer = @unserialize($customer_info['custom_field']);
          if (!$custom_fields_customer) $custom_fields_customer = json_decode($customer_info['custom_field'], true);
        }

		    $custom_fields_ids = $this->load->controller('module/webmaniabrnfe/getCustomFieldsIds');
        $route_get_value_from_fields = 'module/webmaniabrnfe/get_value_from_fields';
        $data_get_value_from_fields = ['key' => 'tipo_pessoa', 'custom_fields_ids' => $custom_fields_ids, 'custom_fields_customer' => $custom_fields_customer,];
        $document_type = $this->load->controller($route_get_value_from_fields, $data_get_value_from_fields);
		    $document = '';
        if($document_type == $this->load->controller($route_get_value_from_fields, array_merge($data_get_value_from_fields, ['key' => 'pessoa_fisica']))){
			    $document = $this->load->controller($route_get_value_from_fields, array_merge($data_get_value_from_fields, ['key' => 'cpf']));
        }elseif($document_type == $this->load->controller($route_get_value_from_fields, array_merge($data_get_value_from_fields, ['key' => 'pessoa_juridica']))){
			    $document = $this->load->controller($route_get_value_from_fields, array_merge($data_get_value_from_fields, ['key' => 'cnpj']));
        }

        foreach ($nfe_info as $key => $nfe) {
          $password = !empty($nfe['documento']) ? $nfe['documento'] : $document;
          if(!empty($password)){
            $tokens = [
              "danfe" => $this->load->controller('module/webmaniabrnfe/createSecureTokenDFe', ["password" => $password, "uuid" => $nfe['uuid']]),
              "xml" => $this->load->controller('module/webmaniabrnfe/createSecureTokenDFe', ["password" => $password, "uuid" => $nfe['uuid']]),
            ];
            $nfe_info[$key]['url_danfe'] = $nfe_info[$key]['url_danfe'] . '?token=' . $tokens['danfe'];
            $nfe_info[$key]['url_xml'] = $nfe_info[$key]['url_xml'] . '?token=' . $tokens['xml'];
          }
        }

        $data['nfe_info'] = $nfe_info;
        $data['url'] = $url;
       }else{
        $data['nfe_info'] = array();
        $data['url'] = '';
       }

if(isset($this->request->get['route'])){
  $route = $this->request->get['route'];
  if($route == 'customer/customer/add' || $route == 'customer/customer/edit' || $route == 'sale/customer/add' || $route == 'sale/customer/edit' || $route == 'sale/order/edit' || $route == 'sale/order/add'){
    $data['run_script'] = true;
    $this->load->model('setting/setting');
		$module_settings = $this->model_setting_setting->getSetting('webmaniabrnfe');
		if(isset($module_settings['webmaniabrnfe_fill_address'])){
			if($module_settings['webmaniabrnfe_fill_address'] == 'on'){
				$data['fill_address'] = true;
			}
		}

		if(isset($module_settings['webmaniabrnfe_mask_fields'])){
			if($module_settings['webmaniabrnfe_mask_fields'] == 'on'){
				$data['mask_fields'] = true;
			}
		}
  }
}

